var checkLoggedOut = function($q, $timeout, $location, $rootScope) {
    var deferred = $q.defer();

    if ($rootScope.user) {
        deferred.resolve();
    } else {
        $timeout(deferred.reject);
        return $location.url('/login');
    }

    return deferred.promise;
};

